import { useEffect, useState } from 'react';
import './App.css';
//'https://jsonplaceholder.typicode.com/todos'
function App() {
  const [posts, setPosts] = useState([])

  useEffect(() => {
     loadPosts()
  })

  const loadPosts = async () => {
    const postsTexts = fetch('https://jsonplaceholder.typicode.com/posts')
    const [posts] = await Promise.all([postsTexts])
    const postsJsonParsed = await posts.json()

    setPosts(postsJsonParsed)
  }

  return (
    <section className="container">


    <div className="posts-container">
      {posts.map(post => {
        return (

          <div key={post.id} className="post-texts">
            <h1>{post.title}</h1>
            <p>{post.body}</p>
          </div>
        )
      })}
    </div>


    </section>
  );
}

export default App;
