import React, { useState, useEffect } from 'react';
import { PostCard } from './PostCard';
import { loadPosts } from '../utils/loadPosts';
import { isDisabled } from '@testing-library/user-event/dist/utils';

export const Posts = () => {
    const [posts, setPosts] = useState([])
    const [fillPosts, setfillPosts] = useState([])
    var [count, setCount] = useState(97)
    const [isDesabled, setIsDisabled] = useState(false)

    useEffect(() => {
     load()
    })

    const load = async () => {
      const postsAndPhotos = await loadPosts()

      const filtedPosts = postsAndPhotos.slice(0, count)
      if(count + 3 > postsAndPhotos.length) {
          setIsDisabled(true)
      }

      setPosts(filtedPosts)
    }

    const loadMore = () => {
       setCount(count = count + 3)
    }

    return (
        <>
        <div className="posts-container">
        {posts.map(post => {
         return (
            <PostCard 
              key={post.id}
              cover={post.cover}
              title={post.title}
              body={post.body}
            />
         )
        })}
        </div>
        <button onClick={loadMore} disabled={isDesabled}>Carregar mais posts</button>
        </>
    )
}
